temtype="http://schema.org/ImageGallery" data-pswp-uid="<?php echo $key; ?>">                    <!--<a class="J_ping" report-eventid="MProductdetail_CommentPictureTab" report-pageparam="1725965683" >-->                    <!--<div class="my-gallery" data-pswp-uid="<?php echo $key; ?>">-->                    <?php if(is_array($vo['img']) || $vo['img'] instanceof \think\Collection || $vo['img'] instanceof \think\Paginator): if( count($vo['img'])==0 ) : echo "" ;else: foreach($vo['img'] as $key=>$v2): ?>                    <figure itemprop="associatedMedia" itemscope itemtype="http://schema.org/ImageObject">                        <a href="<?php echo $v2; ?>" data-size = <?php echo $vo['size'][$key][0]; ?>x<?php echo $vo['size'][$key][1]; ?>>                            <div class="image-container" style="background-image: url(<?php echo $v2; ?>)" ></div>                            <img src="<?php echo $v2; ?>" style="display: none">                        </a>                    </figure>                    <?php endforeach; endif; else: echo "" ;endif; ?>                </div>                <div class="bottom-info">                    <span class='hours'><?php echo $vo['timestr']; ?></span>                    <div class='love-wrapper'>                        <i class="fa fa-heart fa-lg love-icon <?php if($iszan == 1): ?> love_red <?php endif; ?>" value="<?php echo $vo['id']; ?>" aria-hidden="true" onclick="zan(this)"></i>                        <span class="zan"><?php echo $vo['zan']; ?></span>                    </div>                    <div class='msg-count'>                        <i class="fa fa-commenting fa-lg message-icon" aria-hidden="true" value="<?php echo $vo['id']; ?>"></i><span><?php echo $vo['comment']; ?></span>                    </div>                    <div class='vote-wrapper'>                        <input type="button" value='投票' onclick="toupiao(<?php echo $vo['id']; ?> ,this)" >                        <span class="span"><?php echo $vo['vote']; ?></span>票                    </div>                    <!--<span style="float: right">投票数：<strong style="color: #0aba07"><?php echo $vo['vote']; ?></strong></span>-->                    <!--<i class="fa fa-commenting fa-lg message-icon" value="<?php echo $vo['id']; ?>" ><?php echo $vo['comment']; ?></i>-->                    <!--<i class="fa fa-heart fa-lg love-icon <?php if($iszan == 1): ?> love_red <?php endif; ?>" value="<?php echo $vo['id']; ?>"><?php echo $vo['zan']; ?></i>-->                </div>            </div>        </li>        <?php endforeach; endif; else: echo "" ;endif; ?>    </ul>    <?php if(($count > $current_count) AND (count($commentlist) == $page_count)): ?>    <div class="getmore" style="font-size:.32rem;text-align:center;color:#888;padding:.25rem .24rem .4rem; clear:both">        <a href="javascript:void(0)" onClick="ajaxSourchSubmit();">点击加载更多</a>    </div>    <?php elseif(($count <= $current_count AND $count > 0)): ?>    <div class="score enkecor" style="font-size:.32rem;text-align:center;color:#888;padding:.25rem .24rem .4rem; clear:both">已显示完所有内容</div>    <?php else: endif; ?></div><div class="pswp" tabindex="-1" role="dialog" aria-hidden="true">    <!-- Background of PhotoSwipe.         It's a separate element as animating opacity is faster than rgba(). -->    <div class="pswp__bg"></div>    <!-- Slides wrapper with overflow:hidden. -->    <div class="pswp__scroll-wrap">        <!-- Container that holds slides.            PhotoSwipe keeps only 3 of them in the DOM to save memory.            Don't modify these 3 pswp__item elements, data is added later on. -->        <div class="pswp__container">            <div class="pswp__item"></div>            <div class="pswp__item"></div>            <div class="pswp__item"></div>        </div>        <!-- Default (PhotoSwipeUI_Default) interface on top of sliding area. Can be changed. -->        <div class="pswp__ui pswp__ui--hidden">            <div class="pswp__top-bar">                <!--  Controls are self-explanatory. Order can be changed. -->                <div class="pswp__counter"></div>                <button class="pswp__button pswp__button--close" title="Close (Esc)"></button>                <!--<button class="pswp__button pswp__button&#45;&#45;share" title="Share"></button>-->                <button class="pswp__button pswp__button--fs" title="Toggle fullscreen"></button>                <button class="pswp__button pswp__button--zoom" title="Zoom in/out"></button>                <!-- element will get class pswp__preloader--active when preloader is running -->                <div class="pswp__preloader">                    <div class="pswp__preloader__icn">                        <div class="pswp__preloader__cut">                            <div class="pswp__preloader__donut"></div>                        </div>                    </div>                </div>            </div>            <div class="pswp__share-modal pswp__share-modal--hidden pswp__single-tap">                <div class="pswp__share-tooltip"></div>            </div>            <button class="pswp__button pswp__button--arrow--left" title="Previous (arrow left)">            </button>            <button class="pswp__button pswp__button--arrow--right" title="Next (arrow right)">            </button>            <div class="pswp__caption">                <div class="pswp__caption__center"></div>            </div>        </div>    </div></div><!-- 评论区结束 --><!--公共底部--><!--公共底部结束--><script src="/static/home/js/klass.min.js"></script><script>    function toupiao(id , that) {        var num=parseInt($(that).parent().find('.span').text());        $.ajax({            type: "post",            url: "<?php echo url('index/user/toupiao'); ?>",            async:false,            data: {                "id":id            },            success : function(data){                if(data.status == 1){                    num = num+1;                    $(that).parent().find('.span').text(num)                    layer.msg(data.msg,{ time: 2000});                }else{                    layer.msg(data.msg,{ time: 2000});                }//             setTimeout("layer.closeAll()",1500)//             window.location.href="<?php echo url('index/user/photograph'); ?>";            }        });    }    function search() {        var mobile = $("#mobile").val()        window.location.href="<?php echo url('index/user/photograph'); ?>"+"?mobile="+mobile    }</script><script>    var initPhotoSwipeFromDOM = function(gallerySelector) {        // 解析来自DOM元素幻灯片数据（URL，标题，大小...）        // (children of gallerySelector)        var parseThumbnailElements = function(el) {            var thumbElements = el.childNodes,                numNodes = thumbElements.length,                items = [],                figureEl,                linkEl,                size,                item;            for(var i = 0; i < numNodes; i++) {                figureEl = thumbElements[i]; // <figure> element                // 仅包括元素节点                if(figureEl.nodeType !== 1) {                    continue;                }                linkEl = figureEl.children[0]; // <a> element                size = linkEl.getAttribute('data-size').split('x');                // 创建幻灯片对象                item = {                    src: linkEl.getAttribute('href'),                    w: parseInt(size[0], 10),                    h: parseInt(size[1], 10)                };                if(figureEl.children.length > 1) {                    // <figcaption> content                    item.title = figureEl.children[1].innerHTML;                }                if(linkEl.children.length > 0) {                    // <img> 缩略图节点, 检索缩略图网址                    item.msrc = linkEl.children[0].getAttribute('src');                }                item.el = figureEl; // 保存链接元素 for getThumbBoundsFn                items.push(item);            }            return items;        };        // 查找最近的父节点        var closest = function closest(el, fn) {            return el && ( fn(el) ? el : closest(el.parentNode, fn) );        };        // 当用户点击缩略图触发        var onThumbnailsClick = function(e) {            e = e || window.event;            e.preventDefault ? e.preventDefault() : e.returnValue = false;            var eTarget = e.target || e.srcElement;            // find root element of slide            var clickedListItem = closest(eTarget, function(el) {                return (el.tagName && el.tagName.toUpperCase() === 'FIGURE');            });            if(!clickedListItem) {                return;            }            // find index of clicked item by looping through all child nodes            // alternatively, you may define index via data- attribute            var clickedGallery = clickedListItem.parentNode,                childNodes = clickedListItem.parentNode.childNodes,                numChildNodes = childNodes.length,                nodeIndex = 0,                index;            for (var i = 0; i < numChildNodes; i++) {                if(childNodes[i].nodeType !== 1) {                    continue;                }                if(childNodes[i] === clickedListItem) {                    index = nodeIndex;                    break;                }                nodeIndex++;            }            if(index >= 0) {                // open PhotoSwipe if valid index found                openPhotoSwipe( index, clickedGallery );            }            return false;        };        // parse picture index and gallery index from URL (#&pid=1&gid=2)        var photoswipeParseHash = function() {            var hash = window.location.hash.substring(1),                params = {};            if(hash.length < 5) {                return params;            }            var vars = hash.split('&');            for (var i = 0; i < vars.length; i++) {                if(!vars[i]) {                    continue;                }                var pair = vars[i].split('=');                if(pair.length < 2) {                    continue;                }                params[pair[0]] = pair[1];            }            if(params.gid) {                params.gid = parseInt(params.gid, 10);            }            return params;        };        var openPhotoSwipe = function(index, galleryElement, disableAnimation, fromURL) {            var pswpElement = document.querySelectorAll('.pswp')[0],                gallery,                options,                items;            items = parseThumbnailElements(galleryElement);            // 这里可以定义参数            options = {                barsSize: {                    top: 100,                    bottom: 100                },                fullscreenEl : false, // 是否支持全屏按钮                shareButtons: [                    {id:'wechat', label:'分享微信', url:'#'},                    {id:'weibo', label:'新浪微博', url:'#'},                    {id:'download', label:'保存图片', url:'{{raw_image_url}}', download:true}                ], // 分享按钮                // define gallery index (for URL)                galleryUID: galleryElement.getAttribute('data-pswp-uid'),                getThumbBoundsFn: function(index) {                    // See Options -> getThumbBoundsFn section of documentation for more info                    var thumbnail = items[index].el.getElementsByTagName('img')[0], // find thumbnail                        pageYScroll = window.pageYOffset || document.documentElement.scrollTop,                        rect = thumbnail.getBoundingClientRect();                    return {x:rect.left, y:rect.top + pageYScroll, w:rect.width};                }            };            // PhotoSwipe opened from URL            if(fromURL) {                if(options.galleryPIDs) {                    // parse real index when custom PIDs are used                    for(var j = 0; j < items.length; j++) {                        if(items[j].pid == index) {                            options.index = j;                            break;                        }                    }                } else {                    // in URL indexes start from 1                    options.index = parseInt(index, 10) - 1;                }            } else {                options.index = parseInt(index, 10);            }            // exit if index not found            if( isNaN(options.index) ) {                return;            }            if(disableAnimation) {                options.showAnimationDuration = 0;            }            // Pass data to PhotoSwipe and initialize it            gallery = new PhotoSwipe( pswpElement, PhotoSwipeUI_Default, items, options);            gallery.init();        };        // loop through all gallery elements and bind events        var galleryElements = document.querySelectorAll( gallerySelector );        for(var i = 0, l = galleryElements.length; i < l; i++) {            galleryElements[i].setAttribute('data-pswp-uid', i+1);            galleryElements[i].onclick = onThumbnailsClick;        }        // Parse URL and open gallery if it contains #&pid=3&gid=1        var hashData = photoswipeParseHash();        if(hashData.pid && hashData.gid) {            openPhotoSwipe( hashData.pid ,  galleryElements[ hashData.gid - 1 ], true, true );        }    };    // execute above function    initPhotoSwipeFromDOM('.my-gallery');    $(".message-icon").click(function () {        var id = ($(this).attr('value'));        $.ajax({            type: 'post',            url: "<?php echo url('index/discovery/iscomment_login'); ?>?mid=-1&aid="+id,            async:false,            dataType:'json',            success: function(data){                if(data.status==1){                    layer.open({                        type: 1,                        area: ['5.5rem', '4.2rem'],                        title: '评论',                        shadeClose: false, //点击遮罩关闭                        content: '\<\div style="padding:.1rem;" class="comment-window-wrapper"><textarea class="comment-alert" name="comment" id="comment">\<\/textarea>\<input class="comment-alert-submit" type="button" id="comment_sub" value="提交评论">\<\/div>',//                        end: function () {//                            //location.reload();//                            layer.closeAll()//                        }                    });                    //layer.msg(data.msg,{anim:0});                    // <form action="<?php echo url('index/discovery/comment'); ?>&mid=0&aid='+id+'" enctype="multipart/form-data" method="post">                    var index = parent.layer.getFrameIndex(window.name);                    $('#comment_sub').on('click', function(){                        $.ajax({                            type: 'post',                            url: "<?php echo url('index/discovery/comment'); ?>?mid=0&aid=" + id + "&cid=1",                            async: true,                            dataType: 'json',                            data:{comment:$('#comment').val()},                            success:function (res) {                                if(res.status==2){                                    layer.msg(res.msg,{anim:6});                                    setTimeout("layer.closeAll()",1500)                                }else{                                    layer.msg(res.msg,{anim:6});                                    setTimeout("layer.closeAll()",1500)                                }                            }                        })                    });                }else{                    layer.msg(data.msg,{anim:6});                    return false;                }            }        })    })    function zan(that) {        var id = ($(that).attr('value'));        var zannum=parseInt($(that).parent().find('.zan').text());        var a = $(that);        // ($(this).text(2))        $.ajax({            type: 'get',            url: "<?php echo url('index/discovery/comment'); ?>?mid=0&aid="+id,            data:{            },            async:false,            dataType:'json',            success: function(data){                if(data.status==1){                    a.addClass("love_red");                    zannum = (zannum) +1 ;                    ($(that).parent().find('.zan').text(zannum));                    layer.msg(data.msg,{anim:0});                }else{                    layer.msg(data.msg,{anim:6});                }            },            error: function(xhr){                layer.msg('网络错误，请稍后再试');            }        });    }    if(<?php echo $p; ?>){        var page = <?php echo $p; ?>;    }else{        var page = 1 ;    }    function ityzl_SHOW_LOAD_LAYER(){        return layer.msg('努力中...', {icon: 16,shade: [0.5, '#f5f5f5'],scrollbar: false,offset: '0px', time:100000}) ;    }    function ityzl_CLOSE_LOAD_LAYER(index){        layer.close(index);    }    function ajaxSourchSubmit() {        page = page + 1;        var order_bay = <?php echo $order_bay; ?>;        $.ajax({            type: "GET",            url: "<?php echo url('index/user/ajaxphotograph'); ?>"+"?p=" + page+"&order_bay="+order_bay,            beforeSend: function () {                i = ityzl_SHOW_LOAD_LAYER();            },            success: function (data) {                ityzl_CLOSE_LOAD_LAYER(i);                $('.getmore').hide();                if ($.trim(data) != ''){                    $("#comList").append(data);                }            },            error: function (e, jqxhr, settings, exception) {                ityzl_CLOSE_LOAD_LAYER(i);            }        });    }    function  ajax_sourch_submit_hide(){        $('.getmore').hide();    }</script></body></html>